window.onscroll=function(){let e=document.querySelector("header"),r=e.offsetTop;window.pageYOffset>r?e.classList.add("navbar-fixed"):e.classList.remove("navbar-fixed")};const hamburger=document.querySelector("#hamburger"),navMenu=document.querySelector("#nav-menu");hamburger.addEventListener("click",function(){hamburger.classList.toggle("hamburger-active"),navMenu.classList.toggle("hidden")});